s = "abccccdd"  # Output: 7
s = "a"         # Output: 1
s = "bb"        # Output: 1
s = ""


import collections

class Solution:
    def longestPalindrome(self, s: str) -> int:
        keys = collections.Counter(s).most_common()
        key_list_odd = [count for char, count in keys if count%2==0]
        key_even_count = [count-1 for char, count in keys if count%2==1]
        return sum(key_list_odd)+sum(key_even_count)+1 if key_even_count!=[] else sum(key_list_odd)